// http://thrivingkings.com/sticky
//
// http://twitter.com/ThrivingKings

(function( $ )
	{
	
	// Using it without an object
	$.sticky = function(note, _type_message, options, callback) { return $.fn.sticky(note, _type_message, options, callback); };
	
	$.fn.sticky = function(note, _type_message, options, callback) 
		{
		// Default settings
		var position = 'top-center'; // top-left, top-right, bottom-left, or bottom-right
		var type_message = 'st-success';
		
		var settings =
			{
			'speed'			:	'fast',	 // animations: fast, slow, or integer
			'duplicates'	:	false,  // true or false
			'autoclose'		:	3500  // integer or false
			};
		
		// Passing in the object instead of specifying a note
		if(!note)
		{ note = this.html(); }
			
		if(_type_message)
			{ type_message = _type_message; }
		
		if(options)
			{ $.extend(settings, options); }
		
		// Variables
		var display = true;
		var duplicate = 'no';
		
		// Somewhat of a unique ID
		var uniqID = Math.floor(Math.random()*99999);
		
		// Handling duplicate notes and IDs
		$('.sticky-note').each(function()
			{
			if($(this).html() == note && $(this).is(':visible'))
				{ 
				duplicate = 'yes';
				if(!settings['duplicates'])
					{ display = false; }
				}
			if($(this).attr('id')==uniqID)
				{ uniqID = Math.floor(Math.random()*9999999); }
			});
		
		// Make sure the sticky queue exists
		if(!$('#parent_terrain').find('.sticky-queue').html())
			{ $('#parent_terrain').append('<div class="sticky-queue ' + position + '"></div>'); }
		
		// Can it be displayed?
		if(display)
			{
			// Building and inserting sticky note
			$('.sticky-queue').prepend('<div class="sticky border-' + position + ' ' + type_message + '" id="' + uniqID + '"></div>');
			//$('#' + uniqID).append('<img src="close.png" class="sticky-close" rel="' + uniqID + '" title="Close" />');
			$('#' + uniqID).append('<span class="close st-close" rel="'+ uniqID + '" title="Close">Ã—</span>');
			$('#' + uniqID).append('<div class="sticky-note" rel="' + uniqID + '">' + note + '</div>');
			
			// Smoother animation
			var height = $('#' + uniqID).height();
			$('#' + uniqID).css('height', height);
			
			$('#' + uniqID).slideDown(settings['speed']);
			display = true;
			}
		
		// Listeners
		/*$('.sticky').ready(function()
			{
			// If 'autoclose' is enabled, set a timer to close the sticky
			if(settings['autoclose'])
				{ $('#' + uniqID).delay(settings['autoclose']).fadeOut(settings['speed']); }
			alert(settings['autoclose']);
			});*/
			
		// Closing a sticky
		/*$('.sticky-close').click(function()
			{ $('#' + $(this).attr('rel')).dequeue().fadeOut(settings['speed']); });*/
		$('#' + uniqID).delay(settings['autoclose']).fadeOut(settings['speed']);
		
		$('.st-close').click(function()
				{ $('#' + $(this).attr('rel')).dequeue().fadeOut(settings['speed']);});
		
		
		// Callback data
		var response = 
			{
			'id'		:	uniqID,
			'duplicate'	:	duplicate,
			'displayed'	: 	display,
			'position'	:	position
			}
		
		// Callback function?
		if(callback)
			{ callback(response); }
		else
			{ return(response); }
		
		}
	})( jQuery );